class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    long long cost;

    TrieNode() : cost(LLONG_MAX) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, long long cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->cost = min(node->cost, cost);
    }
};

class Solution {
public:
   int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        long long n = target.size();
        Trie trie;
        for (long long i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        vector<long long> dp(n + 1, LLONG_MAX);
        dp[0] = 0;

        for (long long i = 0; i < n; ++i) {
            if (dp[i] == LLONG_MAX) continue;
            TrieNode* node = trie.root;
            for (long long j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                if (node->cost != LLONG_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->cost);
                }
            }
        }

        return dp[n] == LLONG_MAX ? -1 : dp[n];
    }
};
