class TrieNode {
public:
    unordered_map<char, TrieNode*> child;
    vector<pair<int, int>> end;
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(string word, int index, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->child.count(c)) {
                node->child[c] = new TrieNode();
            }
            node = node->child[c];
        }
        node->end.push_back({index, cost});
    }
};

class Solution {
public:
    int  minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i)trie.insert(words[i], i, costs[i]);

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;

            TrieNode* node = trie.root;

            for (int j = i; j < n; j++) {
                if (!node->child.count(target[j]))break;

                node = node->child[target[j]];
                for (auto& p : node->end) {
                    int word_length = words[p.first].size();
                    if (i + word_length <= n) {
                        dp[i + word_length] = min(dp[i + word_length], dp[i] + p.second);
                    }
                }
            }
        }

        return dp[n] == INT_MAX? -1 : dp[n];
    }
};
