package main

import (
	"fmt"
	"math"
)

type TrieNode struct {
	children map[rune]*TrieNode
	wordEnds []WordEnd
}

type WordEnd struct {
	index int
	cost  int
}

type Trie struct {
	root *TrieNode
}

func NewTrie() *Trie {
	return &Trie{root: &TrieNode{children: make(map[rune]*TrieNode)}}
}

func (t *Trie) Insert(word string, index, cost int) {
	node := t.root
	for _, c := range word {
		if _, ok := node.children[c]; !ok {
			node.children[c] = &TrieNode{children: make(map[rune]*TrieNode)}
		}
		node = node.children[c]
	}
	node.wordEnds = append(node.wordEnds, WordEnd{index, cost})
}

func minimumCost(target string, words []string, costs []int) int {
	n := len(target)
	dp := make([]int, n+1)
	for i := range dp {
		dp[i] = math.MaxInt32
	}
	dp[0] = 0

	trie := NewTrie()
	for i := 0; i < len(words); i++ {
		trie.Insert(words[i], i, costs[i])
	}

	for i := 0; i < n; i++ {
		if dp[i] == math.MaxInt32 {
			continue
		}

		node := trie.root
		for j := i; j < n; j++ {
			if _, ok := node.children[rune(target[j])]; !ok {
				break
			}
			node = node.children[rune(target[j])]
			for _, p := range node.wordEnds {
				wordIndex := p.index
				wordCost := p.cost
				length := len(words[wordIndex])
				if i+length <= n {
					dp[i+length] = min(dp[i+length], dp[i]+wordCost)
				}
			}
		}
	}

	if dp[n] == math.MaxInt32 {
		return -1
	}
	return dp[n]
}

func min(a, b int) int {
	if a < b {
		return a
	}
	return b
}
