from typing import List

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        m = len(words)
        word_costs = {}
        for i, word in enumerate(words):
            if word not in word_costs:
                word_costs[word] = costs[i]
            else:
                word_costs[word] = min(word_costs[word], costs[i])
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        for i in range(1, n + 1):
            for word in word_costs:
                if i >= len(word) and target[i - len(word):i] == word:
                    dp[i] = min(dp[i], dp[i - len(word)] + word_costs[word])
        return dp[n] if dp[n] != float('inf') else -1
