import java.util.ArrayList;
import java.util.Arrays;

class Solution {
    static class Node {
        Node[] childArray = new Node[26];
        ArrayList<int[]> infoList = new ArrayList<>();
    }

    int[] memo;

    public int find(String target, String[] words, int[] costs, Node root) {
        for (int i = 0; i < target.length(); i++) {
            if (memo[i] == Integer.MAX_VALUE) {
                continue;
            }
            Node curr = root;
            for (int j = i; j < target.length(); j++) {
                int charIndex = target.charAt(j) - 'a';
                if (curr.childArray[charIndex] == null) {
                    break;
                }
                curr = curr.childArray[charIndex];
                for (int[] wordInfo : curr.infoList) {
                    if (i + wordInfo[0] <= target.length()) {
                        memo[i + wordInfo[0]] = Math.min(memo[i + wordInfo[0]], memo[i] + wordInfo[1]);
                    }
                }
            }
        }

        return memo[target.length()] == Integer.MAX_VALUE ? -1 : memo[target.length()];
    }

    private Node buildTrie(String[] words, int[] costs) {
        Node root = new Node();
        for (int i = 0; i < words.length; i++) {
            Node curr = root;
            for (char c : words[i].toCharArray()) {
                int charIndex = c - 'a';
                if (curr.childArray[charIndex] == null) {
                    curr.childArray[charIndex] = new Node();
                }
                curr = curr.childArray[charIndex];
            }
            curr.infoList.add(new int[]{words[i].length(), costs[i]});
        }
        return root;
    }

    public int minimumCost(String target, String[] words, int[] costs) {
        memo = new int[target.length() + 1];
        Arrays.fill(memo, Integer.MAX_VALUE);
        memo[0] = 0;
        Node root = buildTrie(words, costs);
        return find(target, words, costs, root);
    }
}
