class TrieNode {
public:
    unordered_map<char, TrieNode*> c;
    vector<pair<int, int>> w;
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(string word, int index, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->c.count(c)) {
                node->c[c] = new TrieNode();
            }
            node = node->c[c];
        }
        node->w.push_back({index, cost});
    }
};

class Solution {
public:
    int  minimumCost(string t, vector<string>& words, vector<int>& costs) {
        int n = t.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) {
                continue;
            }

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->c.count(t[j])) {
                    break;
                }
                node = node->c[t[j]];
                for (auto& p : node->w) {
                    int word_len = words[p.first].size();
                    if (i + word_len <= n) {
                        dp[i + word_len] = min(dp[i + word_len], dp[i] + p.second);
                    }
                }
            }
        }

        return dp[n] == INT_MAX? -1 : dp[n];
    }
};
