class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    int min_cost;

    TrieNode() : min_cost(INT_MAX) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->min_cost = min(node->min_cost, cost);
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        Trie trie;

        // Insert all words into the trie
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        // DP array to store minimum cost to form prefix target[0...i-1]
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0; // base case: cost to form an empty prefix is 0

        // Traverse the target string and update dp array
        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue; // If no way to form prefix target[0...i-1], skip

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break; // No valid prefix continuation

                node = node->children[target[j]];
                if (node->min_cost != INT_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->min_cost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
