struct node{
    node* arr[26];
    bool flag;
    int c;

    node(){
        flag=false;
        for(int i=0;i<26;i++) arr[i]=NULL;
        c=1e9;
    }
};

struct Trie{

    node* root;
    Trie() {
        root=new node();
    }

    void insert(string word,int cost) {
        int n=word.size();
        node* cur=root;
        for(int i=0;i<n;i++){
            if(cur->arr[word[i]-'a']==NULL){
                cur->arr[word[i]-'a']=new node();
            }

            cur=cur->arr[word[i]-'a'];
        }

        cur->flag=1;
        cur->c=min(cur->c,cost);
    }

};

class Solution {
public:

    ll dp[5*10000+5];
    ll solve(int i,Trie &ti,string &s){

        if(i==s.size()) return 0ll; 
        if(dp[i] != -1) return dp[i];

        node* cur=ti.root;
        ll ans=1e18;
        for(int ind=i;ind<s.size();ind++){
            if(cur->arr[s[ind]-'a'] == NULL) break;
            cur=cur->arr[s[ind]-'a'];

            if(cur->flag && cur->c <= ans){
                ans=min(ans,solve(ind+1,ti,s) +cur->c);
            }
        }

        return dp[i]=ans;
    }
    int minimumCost(string s, vector<string>& words, vector<int>& costs) {
        memset(dp,-1,sizeof(dp));

        Trie ti;
        for(int i=0;i<costs.size();i++){
            ti.insert(words[i],costs[i]);
        }

        if(solve(0,ti,s)>1e15) return -1;
        return solve(0,ti,s);
    }
};
