struct TrieNode {
    unordered_map<char, TrieNode*> children;
    vector<int> word_indices; 
};

class Trie {
public:
    Trie() : root(new TrieNode()) {}

    void insert(const string &word, int index) {
        TrieNode* node = root;
        for (char c : word) {
            if (node->children.find(c) == node->children.end()) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->word_indices.push_back(index);
    }

    TrieNode* getRoot() {
        return root;
    }

private:
    TrieNode* root;
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int targetLen = target.length();
        vector<int> dp(targetLen + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i);
        }

        for (int i = 0; i < targetLen; ++i) {
            if (dp[i] == INT_MAX) continue;

            TrieNode* node = trie.getRoot();
            for (int j = i; j < targetLen; ++j) {
                if (node->children.find(target[j]) == node->children.end()) break;
                node = node->children[target[j]];
                for (int index : node->word_indices) {
                    int wordLen = words[index].length();
                    dp[j + 1] = min(dp[j + 1], dp[i] + costs[index]);
                }
            }
        }

        return dp[targetLen] == INT_MAX ? -1 : dp[targetLen];
    }
};
