#include <unordered_map>
#include <vector>
#include <string>
#include <climits>
using namespace std;

class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    int cost;

    TrieNode() : cost(INT_MAX) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->cost = min(node->cost, cost);
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        vector<int> dp1(n + 1, INT_MAX);
        dp1[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (dp1[i] == INT_MAX) continue;
            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                if (node->cost != INT_MAX) {
                    dp1[j + 1] = min(dp1[j + 1], dp1[i] + node->cost);
                }
            }
        }

        return dp1[n] == INT_MAX ? -1 : dp1[n];
    }
};
