#include <vector>
#include <string>
#include <unordered_map>
#include <algorithm>
#include <climits>
using namespace std;

class Solution {
public:
    struct TrieNode {
        unordered_map<char, TrieNode*> children;
        unordered_map<int, int> wordCosts; // {length of word, cost}
    };

    class Trie {
    public:
        TrieNode* root;

        Trie() {
            root = new TrieNode();
        }

        void insert(const string& word, int cost) {
            TrieNode* node = root;
            for (char c : word) {
                if (node->children.find(c) == node->children.end()) {
                    node->children[c] = new TrieNode();
                }
                node = node->children[c];
            }
            if (node->wordCosts.find(word.size()) == node->wordCosts.end() || node->wordCosts[word.size()] > cost) {
                node->wordCosts[word.size()] = cost;
            }
        }
    };

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }
        vector<int> memo(target.size(), -1);
        int result = dp(target, 0, trie, memo);
        return result == INT_MAX ? -1 : result;
    }

private:
    int dp(const string& target, int start, Trie& trie, vector<int>& memo) {
        if (start == target.size()) return 0;
        if (memo[start] != -1) return memo[start];

        TrieNode* node = trie.root;
        int minCost = INT_MAX;

        for (int i = start; i < target.size(); ++i) {
            if (node->children.find(target[i]) == node->children.end()) break;
            node = node->children[target[i]];

            for (const auto& [length, cost] : node->wordCosts) {
                if (start + length <= target.size() && length == i - start + 1) {
                    int nextCost = dp(target, start + length, trie, memo);
                    if (nextCost != INT_MAX) {
                        minCost = min(minCost, cost + nextCost);
                    }
                }
            }
        }

        memo[start] = minCost;
        return minCost;
    }
};
