class Solution {
public:
    int dp[50002];
    struct Trie
    {
        Trie *a[26];
        bool flag;
        int val;

        Trie()
        {
            flag = false;
            val = INT_MAX;
            for(int i=0;i<26;i++)
                a[i]=NULL;
        }
    };

    long long solve(int i,string &tar,Trie *root)
    {

        if(i>=tar.size())
            return 0;
        if(dp[i]!=-1)
            return dp[i];

        long long mini =INT_MAX;
            Trie * tem3 =root;
        for(int x=i;x<tar.size();x++)
        {
            if(tem3->a[tar[x]-'a']==NULL)
            {
                return dp[i]=mini;
            }
            else
            {
                tem3 = tem3->a[tar[x]-'a'];
            }

            if(tem3->flag)
            {
                long long chk = solve(x+1,tar,root);

                if(chk == INT_MAX)
                    continue;
                else
                mini =min(mini,(long long)(tem3->val)+chk);
            }
        }

        return dp[i]=mini;
    }
    int minimumCost(string tar, vector<string>& words, vector<int>& costs) {
        Trie *root = new Trie();
        memset(dp,-1,sizeof(dp));

        for(int j=0;j<words.size();j++)
        {
            string s = words[j];

            Trie *tem = root;
            for(int i=0;i<s.size();i++)
            {
                if(tem->a[s[i]-'a']==NULL)
                {
                    Trie *tem2 = new Trie();
                    tem->a[s[i]-'a']= tem2;
                    tem = tem2;
                }
                else
                    tem = tem->a[s[i]-'a'];
            }

            tem ->flag = true;
            tem -> val = min(costs[j],tem->val);
        }

         long long ans = solve(0,tar,root);

             if(ans==INT_MAX)
             return -1;
        else
            return ans;
    }
};
