class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        c = defaultdict(int)
        for k, v in zip(words, costs):
            if k in c:
                c[k] = min(c[k], v)
            else:
                c[k] = v

        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0  

        for i in range(n):
            if dp[i] == float('inf'):
                continue
            for word, cost in c.items():
                if target.startswith(word, i):
                    dp[i + len(word)] = min(dp[i + len(word)], dp[i] + cost)

        return dp[n] if dp[n] != float('inf') else -1
