#include <vector>
#include <string>
#include <unordered_map>
#include <climits>
using namespace std;

class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<pair<int, int>> wordIndices; 

    TrieNode() {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int index, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (node->children.find(c) == node->children.end()) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->wordIndices.push_back({index, cost});
    }

    TrieNode* getRoot() {
        return root;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0; 


        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }


        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue; 

            TrieNode* node = trie.getRoot();
            for (int j = i; j < n && node != nullptr; ++j) {
                if (node->children.find(target[j]) == node->children.end()) break;
                node = node->children[target[j]];
                for (auto& p : node->wordIndices) {
                    int wordIndex = p.first;
                    int cost = p.second;
                    int len = words[wordIndex].size();
                    if (i + len <= n) {
                        dp[i + len] = min(dp[i + len], dp[i] + cost);
                    }
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
