class Solution {
public:
const int inf = 1e9;
template<const int BIT = 26>
struct Trie {
    struct Node {
        int nxt[BIT];
        int cost, cnt;
        Node(){
            for(int x = 0; x < BIT; x++) nxt[x] = -1;
            cost = 1e9;
            cnt = 0;
        }
    };
    vector<Node> trie;
    int sz;
    Trie() {
        trie.push_back(Node());
        sz = 0;
    }

    void insert(string &word, int cost) {
        int cur = 0;
        sz ++;
        for(auto &X : word) {
            int x = X - 'a';
            if(trie[cur].nxt[x] == -1) {
                trie.push_back(Node());
                trie[cur].nxt[x] = trie.size() - 1;
            }
            cur = trie[cur].nxt[x];
            trie[cur].cnt ++;
        }
        trie[cur].cost = min(trie[cur].cost, cost);
    }
};
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size(), m = words.size();
        Trie trie;
        for(int i = 0; i < m; i++) {
            trie.insert(words[i], costs[i]);
        }
        vector<int> dp(n + 1, inf);
        dp[0] = 0;
        for(int i = 0; i < n; i++) {
            int cur = 0;
            for(int j = i; j < n; j++) {
                int x = target[j] - 'a';
                if(trie.trie[cur].nxt[x] == -1) break;
                cur = trie.trie[cur].nxt[x];
                dp[j + 1] = min(dp[j + 1], dp[i] + trie.trie[cur].cost);
            }
        }
        return dp[n] < inf ? dp[n] : -1;
    }
};
