#define pb push_back

class TNode {
public:
    map<char, TNode*> mp;
    vector<pair<int, int>> end;
};

class S1 {
public:
    void func (int i, int n, int lastBit, string& current, vector<string>& result) {
        if (i == n) {
            result.push_back(current);
            return;
        }

        if (lastBit != 0) {
            current.push_back('0');
            func(i + 1, n, 0, current, result);
            current.pop_back(); // backtrack
        }
        current.push_back('1');
        func(i + 1, n, 1, current, result);
        current.pop_back(); // backtrack
    }
};


class T {
public:
    TNode* root;

    T() {
        root = new TNode();
    }

    void inserted (string w, int ind, int c) {
        TNode* node = root;
        for (auto i : w) {
            if ( node->mp.count(i) == 0 ) {
                node->mp[i] = new TNode();
            }
            node = node->mp[i];
        }
        node->end.pb ({ind, c});
    }
};

class Solution {
public:
    int minimumCost(string tar, vector<string>& words, vector<int>& costs) {
        int n = tar.size();
        vector<int> dp(n + 1, 1e9);  // intializing 
        dp[0] = 0;
        int a=0 , b=0;
        T trie;  // obejct 
        int x= words.size();
        for (int i = 0; i < x ; i++ ) {
            trie.inserted (words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == 1e9) continue;
            a+=b;
            TNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if ( node->mp.count(tar[j]) == 0 ) break;
                b+=a;
                node = node->mp[tar[j]];
                a=1;
                for (auto it : node->end) {
                    int w = words[ it.first].size();
                    if (i+1 <= n-w+1 ) {
                        dp[i + w ] = min(dp[i + w], dp[i] + it.second);
                    }
                }
                b=1;
            }
        }
        if( dp[n]== 1e9 ) return -1;
        return dp[n];
    }
};
