class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int trie[100001][27], dp[50001];
        for (int i = 0; i < 100001; i++) {
            for (int j = 0; j < 27; j++)
                trie[i][j] = 0;
        }
        dp[0] = 0;
        for (int i = 1; i < 50001; i++)
            dp[i] = -1;
        int num = 1;
        for (int i = 0; i < words.size(); i++) {
            int idx = 0;
            for (int j = 0; j < words[i].size(); j++) {
                if (trie[idx][words[i][j] - 'a'])
                    idx = trie[idx][words[i][j] - 'a'];
                else {
                    trie[idx][words[i][j] - 'a'] = num;
                    idx = num;
                    num++;
                }
            }
            if (trie[idx][26] == 0)
                trie[idx][26] = costs[i];
            else
                trie[idx][26] = min(trie[idx][26], costs[i]);
        }
        for (int i = 0; i <= target.size(); i++) {
            if (dp[i] == -1)
                continue;
            int idx = 0;
            for (int j = 0; i + j <= target.size(); j++) {
                if (trie[idx][26]) {
                    if (dp[i + j] == -1)
                        dp[i + j] = dp[i] + trie[idx][26];
                    else
                        dp[i + j] = min(dp[i + j], dp[i] + trie[idx][26]);
                }
                if (i + j < target.size() && trie[idx][target[i + j] - 'a'])
                    idx = trie[idx][target[i + j] - 'a'];
                else
                    break;
            }
        }
        return dp[target.size()];
    }
};
