class tri {
    triN par;

    tri() {
        par = new triN();
    }

    void push(String word, int paisa) {
        triN N = par;
        for (char c : word.toCharArray()) {
            N.beta.putIfAbsent(c, new triN());
            N = N.beta.get(c);
        }
        N.paisa = Math.min(N.paisa, paisa);
    }
}
class Solution {
    public int minimumCost(String str, String[] words, int[] paisas) {
        tri tri = new tri();
        int n = str.length();
        for (int i = 0; i < words.length; i++) {
            tri.push(words[i], paisas[i]);
        }

        int[] memo = new int[n + 1];
        Arrays.fill(memo, Integer.MAX_VALUE);
        memo[0] = 0;
        for (int i = 0; i < n; i++) {
            if (memo[i] == Integer.MAX_VALUE) continue;
            triN N = tri.par;
            for (int j = i; j < n; j++) {
                if (!N.beta.containsKey(str.charAt(j))) break;
                N = N.beta.get(str.charAt(j));
                if (N.paisa != Integer.MAX_VALUE) {
                    memo[j + 1] = Math.min(memo[j + 1], memo[i] + N.paisa);
                }
            }
        }
        return memo[n] == Integer.MAX_VALUE ? -1 : memo[n];
    }
}
class triN {
    Map<Character, triN> beta;
    int paisa;
    triN() {
        beta = new HashMap<>();
        paisa = Integer.MAX_VALUE;
    }
}
