class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0  # Cost to form the empty string is 0

        # Create a hash map to store words and their corresponding costs
        word_cost_map = {}
        for word, cost in zip(words, costs):
            if word in word_cost_map:
                word_cost_map[word] = min(word_cost_map[word], cost)
            else:
                word_cost_map[word] = cost

        for i in range(n + 1):
            if dp[i] == float('inf'):
                continue  # Skip if this substring can't be formed
            for word in word_cost_map:
                word_len = len(word)
                if i + word_len <= n and target[i:i + word_len] == word:
                    dp[i + word_len] = min(dp[i + word_len], dp[i] + word_cost_map[word])

        return dp[n] if dp[n] != float('inf') else -1
