struct TrieNode {
    vector<TrieNode*> ch;
    int cost = INT_MAX;
    TrieNode() : ch(26, nullptr) {}
};

class Solution {
private:
    void insert(TrieNode* root, const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->ch[c - 'a'])
                node->ch[c - 'a'] = new TrieNode();
            node = node->ch[c - 'a'];
        }
        node->cost = min(node->cost, cost);
    }

public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        TrieNode* root = new TrieNode();
        int n = target.length();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        for (int i = 0; i < words.size(); ++i) {
            insert(root, words[i], costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = root;
            for (int j = i; j < n && node; ++j) {
                node = node->ch[target[j] - 'a'];
                if (node && node->cost != INT_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->cost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
