import java.util.*;

class TrieNode {
    public TrieNode child[];
    public boolean isEndOfWord;
    public int cost;

    public TrieNode() {
        child = new TrieNode[26];

        for (int i = 0; i < 26; i++) {
            child[i] = null;
        }

        isEndOfWord = false;
        cost = Integer.MAX_VALUE;
    }
}
class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        int n = costs.length;
        TrieNode root = new TrieNode();

        for (int i = 0; i < n; i++) {
            TrieNode ptr = root;

            for (int j = 0; j < words[i].length(); j++) {
                if (ptr.child[(int)words[i].charAt(j) - (int)'a'] == null) {
                    ptr.child[(int)words[i].charAt(j) - (int)'a'] = new TrieNode();
                }
                ptr = ptr.child[(int)words[i].charAt(j) - (int)'a'];
            }
            ptr.isEndOfWord = true;
            ptr.cost = Math.min(ptr.cost, costs[i]);
        }
        int dp[] = new int[target.length() + 1];

        dp[target.length()] = 0;

        for (int i = target.length() - 1; i >= 0; i--) {
            TrieNode ptr = root;
            dp[i] = Integer.MAX_VALUE;

            for (int j = i; j < target.length(); j++) {
                ptr = ptr.child[(int)target.charAt(j) - (int)'a'];
                if (ptr == null) {
                    break;
                } else if (ptr.isEndOfWord && dp[j + 1] < Integer.MAX_VALUE) {
                    dp[i] = Math.min(dp[i], ptr.cost + dp[j + 1]);
                }
            }
        }
       // System.out.println(Arrays.toString(dp));

        return (dp[0] == Integer.MAX_VALUE) ? -1 : dp[0];
    }
}
