class Solution {
public:
    struct TrieNode {
        vector<int> indices;
        TrieNode* children[26] = {nullptr};
    };

    void insert(TrieNode* root, const string& word, int index) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children[c - 'a']) {
                node->children[c - 'a'] = new TrieNode();
            }
            node = node->children[c - 'a'];
        }
        node->indices.push_back(index);
    }

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        // Build trie from words
        TrieNode* root = new TrieNode();
        for (int i = 0; i < words.size(); ++i) {
            insert(root, words[i], i);
        }

        // Traverse the target string
        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = root;
            for (int j = i; j < n; ++j) {
                if (!node->children[target[j] - 'a']) break;
                node = node->children[target[j] - 'a'];
                for (int index : node->indices) {
                    int len = words[index].size();
                    if (j - i + 1 == len) {
                        dp[j + 1] = min(dp[j + 1], dp[i] + costs[index]);
                    }
                }
            }
        }

        return (dp[n] == INT_MAX) ? -1 : dp[n];
    }
};
