func minimumCost(target string, words []string, costs []int) int {
    wordsCostsBelow200Len := map[string]int{}
	longWords := []string{}
	longWordCosts := []int{}
	for index, word := range words {
		if len(word) <= 200 {
			if curCost, ok := wordsCostsBelow200Len[word]; ok {
				if costs[index] < curCost {
					wordsCostsBelow200Len[word] = costs[index]
				}
			} else {
				wordsCostsBelow200Len[word] = costs[index]
			}
		} else {
			longWords = append(longWords, word)
			longWordCosts = append(longWordCosts, costs[index])
		}
	}

	minCost := make([]int, len(target)+1)
	minCost[len(target)] = 0
	for pos := len(target) - 1; pos >= 0; pos-- {
		minCost[pos] = -1
		for e := pos + 1; e <= len(target) && e <= pos+205; e++ {
			subStr := target[pos:e]
			if cost, ok := wordsCostsBelow200Len[subStr]; ok {
				if (minCost[e]) != -1 {
					tempCost := cost + minCost[e]
					if minCost[pos] == -1 || tempCost < minCost[pos] {
						minCost[pos] = tempCost
					}
				}
			}
		}

		for index, word := range longWords {
			wordLen := len(word)
            if pos+wordLen > len(target) {
				continue
			}
			if target[pos:pos+wordLen] == word {
				cost := longWordCosts[index]
				if (minCost[pos+wordLen]) != -1 {
					tempCost := cost + minCost[pos+wordLen]
					if minCost[pos] == -1 || tempCost < minCost[pos] {
						minCost[pos] = tempCost
					}
				}
			}
		}
	}

	return minCost[0]
}
