class vinTrio {
public:
    unordered_map<char, vinTrio*> babie;
    int amount;

    vinTrio() : amount(INT_MAX) {}
};

class Trie {
public:
    vinTrio* root;

    Trie() {
        root = new vinTrio();
    }

    void insert(const string& word, int cost) {
        vinTrio* node = root;
        for (char c : word) {
            if (!node->babie.count(c)) {
                node->babie[c] = new vinTrio();
            }
            node = node->babie[c];
        }
        node->amount = min(node->amount, cost);
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& price) {
        int z = target.size();
        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], price[i]);
        }

        vector<int> dp(z + 1, INT_MAX);
        dp[0] = 0;

        for (int i = 0; i < z; ++i) {
            if (dp[i] == INT_MAX) continue;
            vinTrio* node = trie.root;
            for (int j = i; j < z; ++j) {
                if (!node->babie.count(target[j])) break;
                node = node->babie[target[j]];
                if (node->amount != INT_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->amount);
                }
            }
        }

        return dp[z] == INT_MAX ? -1 : dp[z];
    }
};
