class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        costs_lookup = {}
        lengths_lookup = {}
        for i in range(len(words)):
            w = words[i]
            if w not in costs_lookup or costs_lookup[w] > costs[i]:
                costs_lookup[w] = costs[i]
                lengths_lookup[w] = len(w)

        words = sorted(costs_lookup.keys())
        words.append("|")

        # print(words)
        result = [None] * len(target)

