        right = bisect.bisect_right(words, chr(ord(v) + 1) + "A")

        # print(left, right)
        for i in range(left, right):
            w = words[i]
            if target[:lengths_lookup[w]] == w:
                result[lengths_lookup[w] - 1] = costs_lookup[w]


        # Pos after 0
        for pos in range(1, len(target)):
            if result[pos - 1] == None:
                continue

            v = target[pos]

            left = bisect.bisect_left(words, chr(ord(v) - 1) + "{")
            right = bisect.bisect_right(words, chr(ord(v) + 1) + "A")
            # print("checking",pos, v, words[left:right + 1])

            for i in range(left, right):
                w = words[i]
                length = lengths_lookup[w]
                if target[pos:pos + length] == w:
                    cost_new = result[pos - 1] + costs_lookup[w]
                    cost_old = result[pos + length - 1]
                    if cost_old == None or cost_old > cost_new:
                        result[pos + length - 1] = cost_new


        # print(result)

        if (v := result[-1]) == None:
            return -1

        return v
