class Solution {

    class TrieNode {
        boolean isTerminal;
        int cost;
        Map<Character, TrieNode> next;

        public TrieNode() {
            this.isTerminal = false;
            this.cost = 0;
            this.next = new HashMap<Character, TrieNode>();
        }
    }

    private int minimumCost(char[] arr, int pos, int[] dp, TrieNode root) {
        if(pos == arr.length) return 0;
        if(dp[pos] != 0) return dp[pos];
        int result = Integer.MAX_VALUE;
        TrieNode curr = root;
        int kPos = pos;
        while(kPos < arr.length) {
            if(curr.next.containsKey(arr[kPos])) {
                curr = curr.next.get(arr[kPos]);
                if(curr.isTerminal) {
                    int subRes = minimumCost(arr, kPos + 1, dp, root);
                    if(subRes != Integer.MAX_VALUE) {
                        result = Math.min(result, curr.cost + subRes);
                    }
                }
            } else {
                break;
            }
            kPos++;
        }
        dp[pos] = result;
        return result;
    }

    public int minimumCost(String target, String[] words, int[] costs) {
        TrieNode root = new TrieNode();
        for(int pos = 0; pos < words.length; pos++) {
            TrieNode curr = root;
            for(char c : words[pos].toCharArray()) {
                if(curr.next.containsKey(c)) {
                    curr = curr.next.get(c);
                } else {
                    TrieNode newNode = new TrieNode();
                    curr.next.put(c, newNode);
                    curr = newNode;
                }
            }
            curr.isTerminal = true;
            curr.cost = curr.cost != 0 ? Math.min(curr.cost, costs[pos]) : costs[pos];
        }
        char[] arr = target.toCharArray();
        int[] dp = new int[arr.length];
        int cost = minimumCost(arr, 0, dp, root);
        return cost == Integer.MAX_VALUE ? -1 : cost;
    }
}
