class TrieNode {
public:
    vector<TrieNode*> children;
    int cost;
    bool end;

    TrieNode() : children(26, nullptr), cost(INT_MAX), end(false) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children[c - 'a']) {
                node->children[c - 'a'] = new TrieNode();
            }
            node = node->children[c - 'a'];
        }
        node->cost = min(cost, node->cost);
        node->end = true;
    }

    TrieNode* getRoot() {
        return root;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie trie;
        int n = target.size();
        vector<int> minCost(n + 1, INT_MAX);

        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        minCost[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (minCost[i] == INT_MAX) continue;

            TrieNode* node = trie.getRoot();
            for (int j = i; j < n; ++j) {
                char c = target[j];
                if (!node->children[c - 'a']) break;
                node = node->children[c - 'a'];
                if (node->end) {
                    minCost[j + 1] = min(minCost[j + 1], minCost[i] + node->cost);
                }
            }
        }

        return minCost[n] == INT_MAX ? -1 : minCost[n];
    }
};
