class Rahul {
public:
    vector<Rahul*> children;
    int value;
    bool isEndOfWord;

    Rahul() : children(26, nullptr), value(INT_MAX), isEndOfWord(false) {}
};

class kumar {
public:
    Rahul* root;

    kumar() {
        root = new Rahul();
    }

    void addWord(const string& word, int cost) {
        Rahul* node = root;
        for (char c : word) {
            if (!node->children[c - 'a']) {
                node->children[c - 'a'] = new Rahul();
            }
            node = node->children[c - 'a'];
        }
        node->value = min(cost, node->value);
        node->isEndOfWord = true;
    }

    Rahul* getRoot() {
        return root;
    }
};
class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
             kumar trie;
        int p = target.size();
        vector<int> arr(p + 1, INT_MAX);

        for (int i = 0; i < words.size(); ++i) {
            trie.addWord(words[i], costs[i]);
        }

        arr[0] = 0;

        for (int i = 0; i < p; ++i) {
            if (arr[i] == INT_MAX) continue;

            Rahul* node = trie.getRoot();
            for (int j = i; j < p; ++j) {
                char c = target[j];
                if (!node->children[c - 'a']) break;
                node = node->children[c - 'a'];
                if (node->isEndOfWord) {
                    arr[j + 1] = min(arr[j + 1], arr[i] + node->value);
                }
            }
        }

        return arr[p] == INT_MAX ? -1 : arr[p];
    }
};
