class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        Trie root=new Trie();
        int i=0;
        Map<String, Integer> map=new HashMap<>();
        for(String w: words){
            map.put(w,Math.min(costs[i],map.getOrDefault(w,Integer.MAX_VALUE)));
            insert(root, w,costs[i++]);
        }
        int n=target.length();
        long []dp=new long[n];
        Arrays.fill(dp,Long.MAX_VALUE);
        for(i=0;i<n;i++){
            find(i, root, target, dp);
        }
        // System.out.println(Arrays.toString(dp));
        return dp[n-1]==Long.MAX_VALUE?-1:(int)dp[n-1];
    }
    public void insert(Trie root, String s, int cost){
        for(int i=s.length()-1;i>=0;i--){
            Trie temp=root.hmap.get(s.charAt(i));
            if(temp==null){
                temp=new Trie();
                root.hmap.put(s.charAt(i),temp);
            }    
            root=temp;
        }
        root.end=true;
        root.cost=Math.min(root.cost,cost);
    }
    public void find(int p, Trie root, String s, long[] dp){
        int i=p;
        while(i>=0){
            Trie temp=root.hmap.get(s.charAt(i));
            if(temp==null) return;

            if(temp.end){
                if(i==0){
                    dp[p]=Math.min(dp[p],temp.cost);
                }else if(dp[i-1]<Integer.MAX_VALUE){
                    dp[p]=Math.min(dp[p],dp[i-1]+temp.cost);
                }
            }
            root=temp;
            i--;
        }
    }
}
class Trie{
    HashMap<Character,Trie> hmap;
    boolean end;
    int cost;
    // char c;
    Trie(){
        hmap=new HashMap<>();
        end=false;
        cost=Integer.MAX_VALUE;
    }
}
