class Solution {
public:
    unordered_map<string,int> mp;
    struct node{
        public:
        int e;
        node* cnt[26];
        node(){
            e=INT_MAX;
            for(int i=0;i<26;i++)
                cnt[i]=NULL;
        }

    };
    void insert(string s ,node* root,int cost){
        reverse(s.begin(),s.end());
        node *temp=root;
        for(int i=0;i<s.length();i++){
            if(temp->cnt[s[i]-'a']==NULL){
                node* naya=new node();
                temp->cnt[s[i]-'a']=naya;
            }
            temp=temp->cnt[s[i]-'a'];
        }

        temp->e=min(temp->e,cost);
       // cout<<s<<" "<<temp->e<<endl;
    }
    void check(long long dp[],node* root, string s,int z){

        node* temp=root;

        for(int i=0;i<s.length();i++){

            if(temp->cnt[s[i]-'a']!=NULL){

                temp=temp->cnt[s[i]-'a'];
                //cout<<s.substr(0,i+1)<<" "<<dp[z-i-1]<<" "<<temp->e<< endl;
                if(temp->e!=INT_MAX&&dp[z-i-1]!=INT_MAX)
                dp[z]=min(dp[z-i-1]+temp->e,dp[z]);
            }
            else break;
        }

    }
    int minimumCost(string tar, vector<string>& w, vector<int>& c) {
        node* root=new node();
        for(int i=0;i<c.size();i++){
            insert(w[i],root,c[i]);
        }
        int n=tar.size();
        long long dp[n+1];
        string s="";
        dp[0]=0;
        for(int z=1;z<=n;z++){
            s+=tar[z-1];
            dp[z]=INT_MAX;
            node* temp=root;
            int k=0;
            for(int i=s.length()-1;i>=0;i--){

            if(temp->cnt[s[i]-'a']!=NULL){

                temp=temp->cnt[s[i]-'a'];
                //cout<<s.substr(0,i+1)<<" "<<dp[z-i-1]<<" "<<temp->e<< endl;
                if(temp->e!=INT_MAX&&dp[z-k-1]!=INT_MAX)
                dp[z]=min(dp[z-k-1]+temp->e,dp[z]);
            }
            else break;
                k++;
          }
        }
        if(dp[n]!=INT_MAX)return dp[n];else return -1;
    }
};
