class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n=len(target)
        m=len(words)

        charc={}
        for i, j in enumerate(words):
            charc[j] = min(charc.get(j, float('inf')), costs[i])

        dp = [float('inf')]*(n+1)
        dp[0] = 0

        for i in range (1, n+1):
            for j in charc:
                if i >= len(j) and target[i-len(j):i] == j:
                    dp[i] = min(dp[i], dp[i-len(j)] + charc[j])

        return dp[n] if dp[n] != float('inf') else -1
