class Trie
{
    public:
    Trie *l[26]={};
    int cost=1e9;
    bool isL=false;
    void insert(string s,int x)
    {
        Trie *p=this;
        for(int i=0;i<s.size();i++)
        {
            if(!p->l[s[i]-'a'])
                p->l[s[i]-'a']=new Trie();
            p=p->l[s[i]-'a'];
        }
        p->isL=true;
        p->cost=min(p->cost,x);
    }
};
class Solution {
public:
    int minimumCost(string& s, vector<string>& words, vector<int>& costs) {
        Trie *p=new Trie();
        for(int i=0;i<costs.size();i++)
            p->insert(words[i],costs[i]);
        int n=s.size();
        vector<int> dp(n+1,1e9);
        dp[0]=0;
        for(int i=1;i<=s.size();i++)
        {
            if(dp[i-1]!=1e9)
            {
                int j=i;
                Trie *x=p;
                while(j<=s.size()&&x&&x->l[s[j-1]-'a'])
                {
                    x=x->l[s[j-1]-'a'];
                    if(x->isL)
                    {
                        dp[j]=min(dp[j],dp[i-1]+x->cost);
                    }
                    j++;
                }
            }
        }
        if(dp.back()==1e9)dp.back()=-1;
        return dp.back();

    }
};
