class Solution {
    Integer[] memo;
    TrieNode root;
    public int minimumCost(String target, String[] words, int[] costs) {

        root= new TrieNode();
        for (int i = 0; i<words.length; i++) {
            String word = words[i];
            int cost = costs[i];
            addToTrie(word, cost);
        }
        memo = new Integer[target.length()];
        StringBuilder sb = new StringBuilder();
        int ans = helper(target, sb, 0);
        return ans == Integer.MAX_VALUE ? -1 : ans;
    }
    public void addToTrie(String s, int val) {
        TrieNode cur = root;
        for (int i = 0; i<s.length(); i++) {
            char c = s.charAt(i);
            if (cur.children[c - 'a'] == null) {
                cur.children[c-'a'] = new TrieNode();
            }
            cur = cur.children[c-'a'];
        }
        cur.val = Math.min(cur.val, val);
    }
    public int helper(String target, StringBuilder sb, int index) {
        if (index >= target.length()) {
            return 0;
        }
        if (memo[index] != null) {
            return memo[index];
        }
        int ans = Integer.MAX_VALUE;
        TrieNode cur = root;
        for (int i = index; i<target.length(); i++) {
            char c = target.charAt(i);
            if (cur.children[c-'a'] != null) {
                cur = cur.children[c-'a'];
                if (cur.val != Integer.MAX_VALUE) {
                    int next = helper(target, sb, i + 1);
                    if (next != Integer.MAX_VALUE) {
                        ans = Math.min(ans, next + cur.val);
                    }  
                } 
            } else {
                break;
            }


        }
        return memo[index] = ans;
    }
}
class TrieNode {
    TrieNode[] children;
    int val;
    public TrieNode() {
        children = new TrieNode[26];
        val = Integer.MAX_VALUE;
    }
}
