/**
 * @param {string} target
 * @param {string[]} words
 * @param {number[]} costs
 * @return {number}
 */
var minimumCost = function(target, words, costs) {
    class TrieNode {
        constructor() {
            this.children = {};
            this.wordIndices = [];
        }
    }

    const root = new TrieNode();
    for (let i = 0; i < words.length; i++) {
        let node = root;
        for (let j = words[i].length - 1; j >= 0; j--) {
            const char = words[i][j];
            if (!node.children[char]) {
                node.children[char] = new TrieNode();
            }
            node = node.children[char];
        }
        node.wordIndices.push(i);
    }

    const n = target.length;
    const dp = new Array(n + 1).fill(Infinity);
    dp[0] = 0;

    for (let i = 1; i <= n; i++) {
        let node = root;
        for (let j = i - 1; j >= 0 && node; j--) {
            const char = target[j];
            if (!node.children[char]) break;
            node = node.children[char];

            for (const wordIndex of node.wordIndices) {
                dp[i] = Math.min(dp[i], dp[j] + costs[wordIndex]);
            }
        }
    }

    return dp[n] === Infinity ? -1 : dp[n];
};
