class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word, cost):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.cost = min(node.cost, cost)  # Store the minimum cost for this word

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        trie = Trie()

        # Insert words and their costs into the Trie
        for word, cost in zip(words, costs):
            trie.insert(word, cost)

        # Initialize DP array
        dp = [float('inf')] * (n + 1)
        dp[0] = 0  # Zero cost to form an empty string

        # Dynamic Programming to compute minimum cost
        for i in range(n):
            if dp[i] == float('inf'):
                continue
            # Traverse the Trie from the current position in the target string
            node = trie.root
            j = i
            while j < n and target[j] in node.children:
                node = node.children[target[j]]
                j += 1
                if node.cost != float('inf'):
                    dp[j] = min(dp[j], dp[i] + node.cost)

        return dp[n] if dp[n] != float('inf') else -1
