import java.util.*;

class TrieNode {
    Map<Character, TrieNode> children;
    int minCost;

    public TrieNode() {
        this.children = new HashMap<>();
        this.minCost = Integer.MAX_VALUE;
    }
}

class Trie {
    TrieNode root;

    public Trie() {
        this.root = new TrieNode();
    }

    public void insert(String word, int cost) {
        TrieNode node = root;
        for (char ch : word.toCharArray()) {
            if (!node.children.containsKey(ch)) {
                node.children.put(ch, new TrieNode());
            }
            node = node.children.get(ch);
        }
        node.minCost = Math.min(node.minCost, cost);
    }
}

class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        int targetLength = target.length();
        Trie trie = new Trie();

        // Insert all words into the Trie with their respective costs
        for (int i = 0; i < words.length; i++) {
            trie.insert(words[i], costs[i]);
        }

        // Initialize dp array with infinity
        int[] dp = new int[targetLength + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        // Fill dp array
        for (int i = 0; i < targetLength; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;
            TrieNode node = trie.root;
            for (int j = i; j < targetLength; j++) {
                char ch = target.charAt(j);
                if (!node.children.containsKey(ch)) break;
                node = node.children.get(ch);
                if (node.minCost != Integer.MAX_VALUE) {
                    dp[j + 1] = Math.min(dp[j + 1], dp[i] + node.minCost);
                }
            }
        }

        return dp[targetLength] == Integer.MAX_VALUE ? -1 : dp[targetLength];
    }
}
