class Solution {
public:
    struct Node {
        unordered_map<char, Node*> children;
        vector<pair<int, int>> ends; 
    };

    class Trie {
    public:
        Node* root;

        Trie() {
            root = new Node();
        }

        void insert(string& word, int index, int cost) {
            Node* node = root;
            for (char ch : word) {
                if (!node->children.count(ch))
                    node->children[ch] = new Node();
                node = node->children[ch];
            }
            node->ends.push_back({index, cost});
        }
    };

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;

            Node* node = trie.root;
            for (int j = i; j < n; ++j) {
                char ch = target[j];
                if (!node->children.count(ch)) break;
                node = node->children[ch];
                for (auto& pair : node->ends) {
                    int index = pair.first;
                    int cost = pair.second;
                    int len = words[index].size();
                    if (i + len <= n) {
                        dp[i + len] = min(dp[i + len], dp[i] + cost);
                    }
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
