/**
 * @param {string} target
 * @param {string[]} words
 * @param {number[]} costs
 * @return {number}
 */
var minimumCost = function(target, words, costs) {
       class TrieNode {
        constructor() {
            this.children = new Map();
            this.minCost = Infinity;
        }
    }

    // Build Trie
    const root = new TrieNode();
    for (let i = 0; i < words.length; i++) {
        let node = root;
        for (let char of words[i]) {
            if (!node.children.has(char)) {
                node.children.set(char, new TrieNode());
            }
            node = node.children.get(char);
        }
        node.minCost = Math.min(node.minCost, costs[i]);
    }

    const n = target.length;
    const dp = new Array(n + 1).fill(Infinity);
    dp[0] = 0;

    for (let i = 0; i < n; i++) {
        let node = root;
        for (let j = i; j < n; j++) {
            if (!node.children.has(target[j])) break;
            node = node.children.get(target[j]);
            if (node.minCost !== Infinity) {
                dp[j + 1] = Math.min(dp[j + 1], dp[i] + node.minCost);
            }
        }
    }

    return dp[n] === Infinity ? -1 : dp[n];
};
