class Node {
public:
    vector<Node*> nxt;
    bool isleaf;
    int val;

    Node() {
        nxt.assign(26, NULL);
        isleaf = false;
        val = 1e9;
    }
};

void addToTrie(Node* cur, string s, int cost) {
    int n = (int)s.size();
    for (int j = 0; j < n; j++) {
        int i = s[j]-'a';
        if (cur->nxt[i] == NULL) {
            cur->nxt[i] = new Node();
        } 
        cur = cur->nxt[i];
    }
    cur->isleaf = true;
    cur->val = min(cur->val, cost);
}

class Solution {
public:
    Node* head;

    int minimumCost(string s, vector<string>& words, vector<int>& costs) {
        head = new Node();
        int cnt = (int)words.size();
        for (int i = 0; i < cnt; i++) {
            addToTrie(head, words[i], costs[i]);
        }

        set<int> q;
        q.insert({0,0});
        unordered_map<int,int> mp;
        mp[0] = 0;
        int n = (int)s.size();
        int ressofar = 1e9;
        while (!q.empty()) {
            int idx = *q.begin();
            int val = mp[idx];
            //cout << idx << " -> " << val << "\n";
            q.erase(q.begin());
            if (val > ressofar) {
                continue;
            }
            Node* cur = head;
            for (int j = idx; j < n; j++) {
                int i = s[j]-'a';
                if (cur->nxt[i] == NULL) {
                    break;
                }
                cur = cur->nxt[i];
                if (cur->isleaf) {
                    int newval = val + cur->val;
                    if (mp.find(j+1) == mp.end() ) {
                        q.insert(j+1);
                        mp[j+1] = newval;
                    }
                    mp[j+1] = min(mp[j+1], newval);
                    if (j+1 == n) {
                        ressofar = min(ressofar, newval);
                    }
                } 
            }
        }

        if (ressofar >= 1e9) ressofar = -1;
        return ressofar;
    }
};
