class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        dic = defaultdict(list)
        temp = defaultdict(list)
        for i in range(len(words)):
            temp[words[i]].append(costs[i])

        for i in temp:
            if i in target:
                dic[i[-1]].append([i,min(temp[i])])
        dp = [0]
        for i in range(len(target)):
            tmp = float('inf')
            for j in dic[target[i]]:
                if target[i-len(j[0])+1:i+1] == j[0]:
                    tmp = min(tmp,j[1]+dp[i-len(j[0])+1])
            dp.append(tmp)
        if dp[-1]  == float(inf):
            return -1
        return dp[-1]
