class Solution {
    class Trie{
        Trie[] child = new Trie[26];
        int cost = (int)1e7;
        boolean isWord;
    }
    Trie root;
    int[] dp;
    public int minimumCost(String target, String[] words, int[] costs) {
        root = new Trie();
        int n = words.length;
        dp= new int[target.length()];
        Arrays.fill(dp,-1);
        for(int i=0;i<n;i++){
            String word=words[i];
            Trie curr = root;

            for(char ch : word.toCharArray()){
                int idx = ch-'a';
                if(curr.child[idx]==null){
                    curr.child[idx]=new Trie();
                }
                curr = curr.child[idx];
            }
            curr.cost = Math.min(curr.cost,costs[i]);
            curr.isWord=true;
        }


        int res = helper(target,0);
        if(res<1e9){
            return res;
        }
        return -1;
    }

    int helper(String target, int idx){
        if(idx==target.length()){
            return 0;
        }
        if(dp[idx]!=-1){
            return dp[idx];
        }

        int cost = (int)1e9;

        Trie curr = root;

        for(int i=idx;i<target.length();i++){
            int ch = target.charAt(i)-'a';

            if(curr.child[ch]==null){
                break;
            }
            curr = curr.child[ch];
            if(curr.isWord)
                cost = Math.min(cost, curr.cost+helper(target,i+1));
        }

        return dp[idx]=cost;
    }
}
