class Solution {
public:

    struct Vertex {
        int next[26];
        int cost = 0;
        Vertex() {
            fill(begin(next), end(next), -1);
        }
    };

    vector<Vertex> trie;

    void add_string(string const& s, int val) {
        int v = 0;
        for (char ch : s) {
            int c = ch - 'a';
            if (trie[v].next[c] == -1) {
                trie[v].next[c] = trie.size();
                trie.emplace_back();
            }
            v = trie[v].next[c];
        }
        if(trie[v].cost!=0)
        {
            trie[v].cost = min(trie[v].cost,val);
        }
        else
            trie[v].cost = val;
    }

    int minimumCost(string &tar, vector<string>& words, vector<int>& costs) {
        int n = tar.size();
        vector<int> dp(n+1);
        // dp[i] -> minimum cost for string from i to n-1
        trie.emplace_back();
        for(int i=0;i<words.size();++i){
            add_string(words[i],costs[i]);
        }
        for(int i=n-1;i>=0;--i){
            int v = 0;
            for(int j=i;j<n;++j){
                char ch = tar[j];
                int c = ch-'a';
                if(trie[v].next[c]!=-1){
                    v = trie[v].next[c];
                }
                else
                    break;
                if(trie[v].cost!=0){
                    if(dp[j+1]!=0 || j==n-1){
                        if(dp[i]==0)
                            dp[i] = trie[v].cost+dp[j+1];
                        else
                            dp[i] = min(dp[i],trie[v].cost+dp[j+1]);
                    }
                }
            }
        }
        if(dp[0]==0)
            return -1;
        return dp[0];
    }
};
