class Node{
    public:
    Node * link[26];
    int cost = INT_MAX;
    Node(){
        for(int i=0;i<26;i++)link[i]=NULL;
    }
};
class Trie{
    public:
    Node *root = new Node();

    void insert(string &s,int c){
        Node *temp = root;
        for(auto x:s){
            if(temp->link[x-'a']==NULL){
                Node *cur = new Node();
                temp->link[x-'a']=cur;
            }
            temp=temp->link[x-'a'];
        }
       temp->cost = min(temp->cost,c);
    }
};
class Solution {
public:
    int minimumCost(string s, vector<string>& words, vector<int>& costs) {
        int n  = words.size();
        Trie t;
        for(int i=0;i<n;i++){
            t.insert(words[i],costs[i]);
        }
        n = s.size();
        vector<int>dp(n+1,INT_MAX);
        dp[0]=0;
        Node *root = t.root;
        for(int i=1;i<=n;i++){
            if(dp[i-1]==INT_MAX)continue;
            Node*temp = root;
            for(int j=i;j<=n;j++){
                if(temp->link[s[j-1]-'a']){
                    temp=temp->link[s[j-1]-'a'];
                    if(temp->cost!=INT_MAX)dp[j]=min(dp[j],dp[i-1]+temp->cost);
                }
                else break;
            }            
        }
        if(dp[n]!=INT_MAX)return dp[n];
        return -1;
    }
};
