class TrieNode {
public:
    std::unordered_map<char, TrieNode*> children;
    int cost;
    TrieNode() : cost(INT_MAX) {}
};

class Solution {
private:
    void insertWord(TrieNode* root, const std::string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children[c]) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->cost = std::min(node->cost, cost);
    }

public:
    int minimumCost(std::string target, std::vector<std::string>& words, std::vector<int>& costs) {
        TrieNode* root = new TrieNode();
        for (int i = 0; i < words.size(); ++i) {
            insertWord(root, words[i], costs[i]);
        }

        int n = target.length();
        std::vector<long long> dp(n + 1, LLONG_MAX);
        dp[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (dp[i] == LLONG_MAX) continue;

            TrieNode* node = root;
            for (int j = i; j < n; ++j) {
                if (!node->children[target[j]]) break;
                node = node->children[target[j]];
                if (node->cost != INT_MAX) {
                    dp[j + 1] = std::min(dp[j + 1], dp[i] + node->cost);
                }
            }
        }

        return dp[n] == LLONG_MAX ? -1 : static_cast<int>(dp[n]);
    }
};
