from typing import List

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        hello=True
        sahayak={}
        for inz, wiz in enumerate(words):

            sahayak[wiz] = min(sahayak.get(wiz, float('inf')), costs[inz])
            hello=True

        h=[float('inf')]*(len(target) + 1)
        h[0] = 0
        temp=True
        flag=True
        for inz in range(1,len(target)+ 1):
            temp=True
            for l in sahayak:

                if inz>=len(l) and target[inz-len(l):inz] == l:
                    flag=False
                    h[inz]=min(h[inz], h[inz-len(l)] + sahayak[l])

        return h[len(target)] if h[len(target)] != float('inf') else -1
