class Trie:
    def __init__(self, c):
        self.c = c
        self.ch = {}
        self.co = 10**10
class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        T = Trie("#")
        for w, co in zip(words, costs):
            root = T
            for c in w:
                if c not in root.ch:
                    root.ch[c] = Trie("c")
                root = root.ch[c]
            root.co = min(root.co, co)
        tl = len(target)
        @lru_cache(maxsize = None)
        def getS(t):
            if t == tl: return 0
            root = T
            ans = 10**10
            for i in range(t, tl):
                if target[i] not in root.ch:
                    return ans
                root = root.ch[target[i]]
                if root.co<10**9:
                    ans = min(ans, getS(i+1) + root.co)
            if root.co<10**9:
                ans = min(ans,  root.co)
            return ans
        ret  = getS(0)
        #print(getS("abdefabc"))
        return -1 if ret > 10**9 else ret
