# https://leetcode.com/problems/word-break/

class Solution:
    # dp, time O(T*W), space O(T)
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        dp = [inf] * (n + 1)
        dp[0] = 0
        for i in range(n):
            if dp[i] == inf:
                continue
            for w, c in zip(words, costs):
                if target.startswith(w, i):
                    dp[i + len(w)] = min(dp[i + len(w)], dp[i] + c)
        return dp[-1] if dp[-1] != inf else -1

    # dp with trie, time O(T*W), space O(T)
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        class TrieNode:
            def __init__(self):
                self.children = {}
                self.is_word = False
                self.cost = inf

        class Trie:
            def __init__(self):
                self.root = TrieNode()

            def insert(self, word, cost):
                node = self.root
                for c in word:
                    if c not in node.children:
                        node.children[c] = TrieNode()
                    node = node.children[c]
                node.is_word = True
                node.cost = cost

        n = len(target)
        dp = [inf] * (n + 1)
        dp[0] = 0

        word_to_cost = {}
        for w, c in zip(words, costs):
            word_to_cost[w] = min(word_to_cost.get(w, inf), c)

        T = Trie()
        for w, c in word_to_cost.items():
            T.insert(w, c)

        for i in range(n):
            if dp[i] == inf:
                continue
            node = T.root
            j = i
            while j < n and target[j] in node.children:
                node = node.children[target[j]]
                j += 1
                if node.is_word:
                    dp[j] = min(dp[j], dp[i] + node.cost)

        return dp[-1] if dp[-1] != inf else -1
