class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        int[] dp = new int[target.length()];
        for (int i = 0; i < dp.length; i++) {
            dp[i] = -1;
        }
        HashMap<String, Integer> map = new HashMap<String, Integer>();
        for (int i = 0; i < words.length; i++) {
            map.put(words[i], Math.min(costs[i], map.getOrDefault(words[i], Integer.MAX_VALUE)));
        }

        for (String a: map.keySet()) {
            if (target.startsWith(a)) {
                //System.out.println(map.get(a));
                if (dp[a.length()-1] == -1) {
                    dp[a.length()-1] = map.get(a);
                }
                else {
                    dp[a.length()-1] = Math.min(dp[a.length()-1], map.get(a));
                }
            }
        }
        /*
        for(int i = 0; i < dp.length; i++) {
            System.out.println(dp[i]);
        }
        System.out.println();
        */
        for (int i = 0; i < dp.length; i++) {
            if (dp[i] == -1) {
                continue;
            }
            for (String a: map.keySet()) {
                if (target.startsWith(a, i+1)) {
                    if (dp[i+a.length()] == -1) {
                        dp[i+a.length()] = dp[i] + map.get(a);
                    }
                    else {
                        dp[i+a.length()] = Math.min(dp[i+a.length()], dp[i] + map.get(a));
                    }
                }
            }
        }
        /*
        for(int i = 0; i < dp.length; i++) {
            System.out.println(dp[i]);
        }
        */
        return dp[dp.length-1];

    }
}
