class Node {
public:
    unordered_map<char, Node*> children;
    vector<pair<int, int>> word;
};

class Trie {
public:
    Node* root;

    Trie() {
        root = new Node();
    }

    void insert(string word, int index, int cost) {
        Node* node = root;
        for (auto it : word) {
            if (!node->children.count(it)) {
                node->children[it] = new Node();
            }
            node = node->children[it];
        }
        node->word.push_back({index, cost});
    }
};

class Solution {
public:
    int  minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); i++) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;

            Node* node = trie.root;
            for (int j = i; j < n; j++) {
                if (!node->children.count(target[j])) {
                    break;
                }
                node = node->children[target[j]]; for (auto &it : node->word) {
                    int len = words[it.first].size();
                    // cout << len << endl;
                    if (i + len <= n) {
                        dp[i + len] = min(dp[i + len], dp[i] + it.second);
                    }
                }
            }
        }

        return dp[n] == INT_MAX? -1 : dp[n];
    }
};
